from pathlib import Path
import sqlite3


db = None
cursor = None


def init_db():
    global db, cursor
    db_path = Path(__file__).parent.parent / 'goods.db'
    db = sqlite3.connect(db_path)
    cursor = db.cursor()
    db.commit()


def create_table():
    cursor.execute(f'''CREATE TABLE IF NOT EXISTS goods(
    key INTEGER PRIMARY KEY AUTOINCREMENT,
    product TEXT,
    description TEXT,
    price INTEGER,
    png TEXT
    )''')
    db.commit()


def check_table():
    cursor.execute('''SELECT * FROM goods''')
    check = cursor.fetchall()
    print(check)
    db.commit()


def fill_table():
    cursor.execute(f'''INSERT INTO goods(product, description, price, png) VALUES
    ('Скетчбук', 'Хороший выбор для творческой личности', 400, 'data:image/jpeg;base64,')''')
    db.commit()  #В этой функции я заполнял все вручную


if __name__ == '__main__':
    init_db()
    create_table()
